real y;

pair P = (8,4);
path parab = ((-2)^2/2,-2);

for (y = -2; y <= 5; y = y + 0.01) {
  parab = parab--(y^2/2,y);
}

draw(parab,red);

draw((-2,0)--(15,0));
draw((0,-2)--(0,15));
draw(P--(6,12));
draw((P + (-4,-4/4))--(P + (4,4/4)),dashed);

dot("$(6,12)$", (6,12), N);
dot("$P$", P, S);
