real t = 40, k = Cos(t);

triple A, B, C, O, P, Q;

A = (Cos(t/2),Sin(t/2),0);
B = (Cos(t/2),-Sin(t/2),0);
C = (k/Cos(t/2),0,sqrt(1 - k^2/Cos(t/2)^2));
O = (0,0,0);
P = (k/Cos(t/2),0,0);
Q = k/(k + 1)*A + k/(k + 1)*B;

draw(O--A,Arrow3(6));
draw(O--B,Arrow3(6));
draw(O--C,Arrow3(6));
draw(O--P,Arrow3(6));
draw(C--P,dashed);

